import React, { Component, Fragment } from "react";
// import { Link } from "react-router-dom";
import Carousel from "../../Components/Homepage/Carousel";
import SearchBar from "../../Components/Homepage/SearchBar";
import "./Home.scss";
import MovieRender from '../../Components/Homepage/MovieRender/index'
import Data from "../../../Data/Data";

class Home extends Component {
    constructor(props){
        super(props)
        this.state = {
           popularMovies:[]
        }
    }

    componentDidMount = async () => {
      let res = await Data.getPopularMovies();
      let data = [];
      for (let i = 0; i < 15; i++) {
        data.push(res.data.results[i]);
      }
      this.setState({ popularMovies: data }, () => {
        console.log(this.state.popularMovies);
      }); // lấy data film popular và lấy ra 6 phim setState
    };


  render() {
    return (
      <Fragment>
        <div className="title">
          <img
            className="logo"
            src={process.env.PUBLIC_URL + "Images/ReactLogo.jpg"}
            alt=""
          />
          <h4>Movie Finder</h4>
        </div>
        <Carousel/>
        <SearchBar />
        <MovieRender />
      </Fragment>
    );
  }
}

export default Home;
