import React, { Component } from "react";
import { Fragment } from "react/cjs/react.production.min";
import "./MovieTag.scss";
import TrailerHover from "./TrailerHover";

class MovieTag extends Component {
  onClickMovie = () => {
    this.props.history.push(`/movie/${this.props.popularMovies.id}`);
  };

  render() {
    const { poster_path, original_title, original_language, release_date, id } =
      this.props.popularMovies;
    return (
      <Fragment>
        {this.props.onTrailer ? <TrailerHover display="block" id={this.props.keys}/> : <TrailerHover display="none" /> }
        <div className="movie-tag">
          <div className="play-button" onClick={() => this.props.onGetId(id)}>
            <img
              src={process.env.PUBLIC_URL + "Images/playbutton.png"}
              alt=""
            />
          </div>
          <img
            src={`https://image.tmdb.org/t/p/original${poster_path}`}
            alt=""
          />
          <div onClick={this.onClickMovie} className="movie-name">
            <p>P</p>
            <h3>{original_title}</h3>
          </div>
          <div className="movie-release">
            <p>Language: {original_language}</p>
            <p>{release_date}</p>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default MovieTag;
