import React, { Component } from "react";
import Slider from "react-slick";
import Data from "../../../Data/Data";
import "./Carousel.scss";

export default class Carousel extends Component {
  constructor(props) {
    super(props);
    this.play = this.play.bind(this);
    this.state = {
      popularMovies: [],
      isLoading:true
    };
  }
  play() {
    this.slider.slickPlay();
  }

  componentDidMount = async () => {
    this.setState({isLoading:true})
    let res = await Data.getPopularMovies();
    let data = [];
    for (let i = 0; i < 6; i++) {
      data.push(res.data.results[i]);
    }
    this.setState({ popularMovies: data,isLoading:false }) // lấy data film popular và lấy ra 6 phim setState
  };

  renderPopularMovies = () => {
    return this.state.popularMovies.map((el) => {
      return (
        <div className="popular-container" key={el.id}>
            <img
            className ="popular-image"
              src={`https://image.tmdb.org/t/p/original${el.backdrop_path}`}
              alt=""
            />
            <div className="description-container">
              <h3 className="popular-name">{el.original_title}</h3>
              <h3 className="popular-description">
                {el.overview}
              </h3>
            </div>
          </div>
      );
    });
  };

  render() {
    const settings = {
      dots: false,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2000,
      adaptiveHeight:true,  
    };
    return (
      <div>

        <Slider ref={(slider) => (this.slider = slider)} {...settings}>
        {this.state.isLoading ? <div><img style={{maxWidth:"30%",a}} src={process.env.PUBLIC_URL + "/Images/loading.gif"} alt=""/></div> : this.renderPopularMovies()}
        </Slider>
        <div style={{ textAlign: "center" }}></div>
      </div>
    );
  }
}
