import React, { Component } from "react";
import Slider from "react-slick";
import Data from "../../../Data/Data";
import "./Carousel.scss";

export default class AutoPlayMethods extends Component {
  constructor(props) {
    super(props);
    this.play = this.play.bind(this);
    this.state = {
      popularMovies: [],
    };
  }
  play() {
    this.slider.slickPlay();
  }


  renderPopularMovies = () => {
    let carouselPopularMovies = []
    for(let i=0;i<5;i++){
      carouselPopularMovies.push(this.props.popularMovies[i])
    }
   console.log(carouselPopularMovies)
    this.setState({popularMovies:carouselPopularMovies})
    return this.state.popularMovies.map((el) => {
      return (
        <div className="popular-container">
            <img
            className ="popular-image"
              src={`https://image.tmdb.org/t/p/original${el.backdrop_path}`}
              alt=""
            />
            <div className="description-container">
              <h3 className="popular-name">{el.original_title}</h3>
              <h3 className="popular-description">
                {el.overview}
              </h3>
            </div>
          </div>
      );
    });
  };

  render() {
    const settings = {
      dots: false,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 3000,
      adaptiveHeight:true,  
    };
    return (
      <div>
        <Slider ref={(slider) => (this.slider = slider)} {...settings}>
          {this.renderPopularMovies()}
        </Slider>
        <div style={{ textAlign: "center" }}></div>
      </div>
    );
  }
}
