import React, { Component } from "react";
import FilmInfo from "../../Components/FilmInfoPage/FilmInfo";
import Data from "../../Data/Data";
import "./Movie.scss";

class Movie extends Component {
  constructor(props){
    super(props)
    this.state = {
      movieInfo:{}
    }
  }

    onBackToHome = () =>{
        this.props.history.push("/")
    }

componentDidMount = async () => {
    let resMovieInfo = await Data.getMovieInfo(this.props.id)
    this.setState({movieInfo:resMovieInfo.data})
    // original_title, title, overview, vote_average,budget,revenue,runtime
}

  render() {
    const {runtime} = this.state.movieInfo.
    return (
      <div>
          <div className="movie-header-container">
              <div className="movie-header"><button onClick={this.onBackToHome}>Home</button><span>| {this.state.movieInfo.title}</span></div>
          </div>
        <FilmInfo movieInfo={this.state.movieInfo}/>
        <div className="budget-container">
          <div><img src={process.env.PUBLIC_URL + "/Images/clock.png"} alt=""/><span>{}</span></div>
          <div><img src={process.env.PUBLIC_URL + "/Images/budget.png"} alt=""/><span>132hours</span></div>
          <div><img src={process.env.PUBLIC_URL + "/Images/money.png"} alt=""/><span>132hours</span></div>
        </div>
        <h3></h3>
      </div>
    );
  }
}

export default Movie;
