        <img src={process.env.PUBLIC_URL + "Images/playbutton.png"} alt="" />
      </div>
      <img src={`https://image.tmdb.org/t/p/original${this.state.popularMovies[0].poster_path}`} alt="" />
      <div src="#" className="movie-name">
        <p>P</p>
        <h3>{this.state.popularMovies[0].original_title}</h3>
      </div>
      <div className="movie-release">
        <p>Language: {this.state.popularMovies[0].original_language}</p>
        <p>{this.state.popularMovies[0].release_date}</p>
      </div>
    </div>)


  }

  render() {
    console.log(this.state.popularMovies[0])
    return (
      <div className="movie-render-container">
          {/* {this.renderMovieTag()} */}
      </div>
    );
  }
}

export default MovieRender;
