import React, { Component } from "react";
import "./MovieRender.scss";
import MovieTag from "./MovieTag";
import Data from '../../../Data/Data'

class MovieRender extends Component {
  constructor(props) {
    super(props);
    this.state = {
      popularMovies: [],
    }
  }

  async componentDidMount() {
    let res = await Data.getPopularMovies();
    let data = [];
    for (let i = 0; i < 12; i++) {
      data.push(res.data.results[i]);
    }
    this.setState({ popularMovies: data },()=>{
    })
  }

  renderMovieTag = () => {
      return this.state.popularMovies.map(el=>{
        return <MovieTag popularMovies={el} key={el.id}/>
      })
  }

  render() {
    return (
      <div className="movie-render-container">
          {/* {this.renderMovieTag()} */}
          <div className="movie-tag">
        <div className="play-button">
          <div className="trailer"><iframe src="https://www.youtube.com/embed/hA6hldpSTF8" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"></iframe></div>
          <img src={process.env.PUBLIC_URL + "Images/playbutton.png"} alt="" />
        </div>
        <img src={`https://image.tmdb.org/t/p/original${this.state.popularMovies[0].poster_path}`} alt="" />
        <a className="movie-name">
          <p>P</p>
          <h3>{original_title}</h3>
        </a>
        <div className="movie-release">
          <p>Language: {original_language}</p>
          <p>{release_date}</p>
        </div>
      </div>
      </div>
    );
  }
}

export default MovieRender;
